import React, { useEffect, useState } from "react";
import { ethers } from "ethers";

function WalletBalance({ walletAddress }) {
  const [balance, setBalance] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!walletAddress) return;

    async function fetchBalance() {
      try {
        const provider = new ethers.BrowserProvider(window.ethereum);
        const balanceBigInt = await provider.getBalance(walletAddress);
        const balanceEther = ethers.formatEther(balanceBigInt);
        setBalance(balanceEther);
      } catch (err) {
        setError("Gagal mengambil balance: " + err.message);
      }
    }

    fetchBalance();

    const interval = setInterval(fetchBalance, 15000);
    return () => clearInterval(interval);
  }, [walletAddress]);

  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <strong>Wallet Balance:</strong>{" "}
      {balance === null ? "Loading..." : `${balance} ETH`}
    </div>
  );
}

export default WalletBalance;
