import React, { useEffect, useState } from 'react';
import { Web3Provider } from '@ethersproject/providers';
import { formatEther } from 'ethers/lib/utils.js';

function WalletBalance({ walletAddress }) {
  const [balance, setBalance] = useState('0.0');
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!walletAddress) return;

    const provider = new Web3Provider(window.ethereum);

    async function fetchBalance() {
      try {
        const balanceBigNumber = await provider.getBalance(walletAddress);
        const balanceInEther = formatEther(balanceBigNumber);
        setBalance(parseFloat(balanceInEther).toFixed(4));
        setError(null);
      } catch (err) {
        setError('Gagal mengambil saldo');
      }
    }

    fetchBalance();

    const interval = setInterval(fetchBalance, 10000);

    return () => clearInterval(interval);
  }, [walletAddress]);

  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return <div>Wallet Balance: {balance} ETH</div>;
}

export default WalletBalance;
