import React, { useEffect, useState } from 'react';

export default function AutoTopupToggle({ walletAddress }) {
  const [enabled, setEnabled] = useState(false);

  useEffect(() => {
    // Fetch current status auto-topup dari backend
    fetch(`http://localhost:8000/autotopup/status/${walletAddress}`)
      .then(res => res.json())
      .then(data => {
        if (data.enabled !== undefined) {
          setEnabled(data.enabled);
        }
      })
      .catch(err => console.error('Failed to fetch auto top-up status', err));
  }, [walletAddress]);

  const toggleStatus = () => {
    fetch(`http://localhost:8000/autotopup/toggle/${walletAddress}`, {
      method: 'POST',
    })
      .then(res => res.json())
      .then(data => {
        if (data.enabled !== undefined) {
          setEnabled(data.enabled);
        }
      })
      .catch(err => console.error('Failed to toggle auto top-up', err));
  };

  return (
    <div>
      <h2>Auto Top-up</h2>
      <p>Status: {enabled ? 'Enabled' : 'Disabled'}</p>
      <button onClick={toggleStatus}>
        {enabled ? 'Disable' : 'Enable'} Auto Top-up
      </button>
    </div>
  );
}
