import React, { useState } from 'react';
import WalletLogin from './WalletLogin';
import WalletBalance from './WalletBalance';
import SwapComponent from './SwapComponent';
import AutoTopupToggle from './AutoTopupToggle';

function App() {
  const [walletAddress, setWalletAddress] = useState('');

  console.log('Wallet Address from App:', walletAddress);

  return (
    <div>
      <h1>AutoFlow Credit</h1>
      <WalletLogin setWalletAddress={setWalletAddress} />
      {walletAddress ? (
        <>
          <WalletBalance walletAddress={walletAddress} />
          <SwapComponent walletAddress={walletAddress} />
          <AutoTopupToggle walletAddress={walletAddress} />
        </>
      ) : (
        <p>Please connect your wallet to continue.</p>
      )}
    </div>
  );
}

export default App;
