import React, { useState } from "react";
import WalletLogin from "./WalletLogin";
import SwapComponent from "./SwapComponent";
import AutoTopupToggle from "./AutoTopupToggle";
import WalletBalance from "./WalletBalance"; // Import komponen baru

function App() {
  const [userAddress, setUserAddress] = useState("");

  return (
    <div>
      <h1>AutoFlow Credit</h1>
      <WalletLogin onLogin={setUserAddress} />
      {userAddress && (
        <>
          <WalletBalance walletAddress={userAddress} />
          <SwapComponent userAddress={userAddress} />
          <AutoTopupToggle walletAddress={userAddress} />
        </>
      )}
    </div>
  );
}

export default App;
